import { useState } from "react";



const Question = () => {

    const tab = {
        images : [
            "../../images/1.png",
            "../../images/2.png",
            "../../images/3.png",
            "../../images/4.png",
            "../../images/5.png",
            "../../images/6.png",
            "../../images/7.png",
            "../../images/8.png"
        ],
        index : "../../images/1.png",

        cpt : 0

    }

    const [index, setindex] = useState(tab)
    console.log(index.images[index.index], "nfizan")
    return (

        <div>
            <button onClick={() => (Random()) } >TEST </button>
            <img src={index.index} alt="test"/>
        </div>

    );

    function Random() {
        let randomnumber = 0
        if(index.images.lenght !== 0) {
            randomnumber = Math.floor(Math.random()*(index.images.length-1))
        }
        let urlactuel = index.images[randomnumber]

        console.log(index.images[randomnumber])
        const images = index.images.filter((value, index ) => {
            console.log(value, index)
            return randomnumber !== index
        })
        console.log(images)
        console.log(randomnumber)
        const test = {
            images:images,
            index:urlactuel
        }
        setindex(test)

    }
}

export default Question
