import "./questions.css" 
import { useState } from "react";



const Question = () => {

    const tab = {
        images : [
            "../../images/1.png",
            "../../images/2.png",
            "../../images/3.png",
            "../../images/4.png",
            "../../images/5.png",
            "../../images/6.png",
            "../../images/7.png",
            "../../images/8.png"
        ],
        index : 0
    }

    const [index, setindex] = useState(tab)
    return (

        <div>
            <button onClick={() => (Random()) } >TEST </button>
            <img src={index.images[index.index]} alt="test"/>
        </div>

    );

    function Random() {
        const randomnumber = Math.floor(Math.random()*(index.images.length))
        setindex({
            images:index.images.filter((value, index ) => {
                console.log(value, index)
                return randomnumber !== index
            }), 
            index:randomnumber
        })

    }
}

export default Question
