import "./questions.css" 
import { useState } from "react";



const Question = () => {

    const tab = [
        "/src/images/1.png"
    ]

    const [index, setindex] = useState(0)
    console.log(tab[index]);
    return (
        <div>
            <button onClick={() => (Random()) } >TEST </button>
            <img src={tab[index]} alt="test"/>
        </div>

    );

    function Random() {
        setindex(Math.floor(Math.random()*(tab.length)))
    }
}

export default Question
