import "./questions.css" 
import { useState } from "react";



const Question = () => {

    var tab = [
        "../../images/1.png",
        "../../images/2.png",
        "../../images/3.png",
        "../../images/4.png",
        "../../images/5.png",
        "../../images/6.png",
        "../../images/7.png",
        "../../images/8.png"
    ]

    const [index, setindex] = useState(0)
    return (

        <div>
            <button onClick={() => (Random()) } >TEST </button>
            <img src={tab[index]} alt="test"/>
        </div>

    );

    function Random() {
        const randomnumber = Math.floor(Math.random()*(tab.length))
        setindex(randomnumber)
        tab.splice(randomnumber,1)
    }
}

export default Question
